INSERT INTO attractions (name, coords, type, subtype, interactivity, time_it_takes, rating, description) VALUES
    ('Zamek Książ', POINT(50.8418, 16.2902), 'urbanistyka', 'zamek', 9, 180, NULL, 'Zamek Książ to jedna z największych atrakcji Dolnego Śląska, położona w malowniczym parku krajobrazowym. Zamek oferuje bogatą historię, piękną architekturę i możliwość eksploracji na długie godziny.'),
    ('Kościół Pokoju w Świdnicy', POINT(50.8522, 16.4850), 'urbanistyka', 'kościół', 8, 90, NULL, 'Kościół Pokoju w Świdnicy to unikatowe dzieło architektury sakralnej, wpisane na Listę Światowego Dziedzictwa UNESCO. Jego wnętrze zachwyca bogatym wystrojem i atmosferą pokoju i spokoju.'),
    ('Panorama Racławicka', POINT(51.1079, 17.0478), 'urbanistyka', 'muzeum', 7, 60, NULL, 'Panorama Racławicka to monumentalne dzieło sztuki, przedstawiające Bitwę pod Racławicami. Jest to niezwykłe doświadczenie dla miłośników historii i sztuki.'),
    ('Śnieżka', POINT(50.7365, 15.7400), 'natura', 'szczyt górski', 10, 240, NULL, 'Śnieżka to najwyższy szczyt Karkonoszy, oferujący zapierające dech w piersiach widoki i możliwość aktywnego wypoczynku w otoczeniu przyrody.'),
    ('Szczeliniec Wielki', POINT(50.4817, 16.3425), 'natura', 'formacja skalna', 9, 120, NULL, 'Szczeliniec Wielki to unikatowa formacja skalna w Górach Stołowych, stanowiąca doskonałą trasę wspinaczkową i punkt widokowy.'),
    ('Błędne Skały', POINT(50.4819, 16.3442), 'natura', 'formacja skalna', 8, 60, NULL, 'Błędne Skały to grupa unikalnych formacji skalnych, które zachwycają swoją różnorodnością i kształtem.'),
    ('Zamek Czocha', POINT(51.0350, 15.3050), 'urbanistyka', 'zamek', 9, 120, NULL, 'Zamek Czocha to imponująca warownia położona nad Jeziorem Leśniańskim, która zachwyca swoją architekturą i bogatą historią.'),
    ('Kopalnia Złota w Złotym Stoku', POINT(50.4431, 16.8672), 'urbanistyka', 'kopalnia', 7, 120, NULL, 'Kopalnia Złota w Złotym Stoku to fascynujące miejsce, gdzie można poznać historię górnictwa i poszukiwać skarbów pod ziemią.'),
    ('Jaskinia Niedźwiedzia', POINT(50.4472, 16.8450), 'natura', 'jaskinia', 9, 90, NULL, 'Jaskinia Niedźwiedzia to malownicza jaskinia w Górach Sowich, która zachwyca bogactwem formacji skalnych i tajemniczą atmosferą.'),
    ('Twierdza Kłodzko', POINT(50.4347, 16.6616), 'urbanistyka', 'twierdza', 8, 120, NULL, 'Twierdza Kłodzko to imponująca warownia, która ma bogatą historię i stanowi doskonały przykład architektury obronnej.'),
    ('Zamek Grodno', POINT(50.6845, 16.3640), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek Grodno to malownicza warownia położona na wzgórzu nad Bobrem, oferująca piękne widoki na okolicę.'),
    ('Sanktuarium w Krzeszowie', POINT(50.8644, 16.2922), 'urbanistyka', 'sanktuarium', 8, 60, NULL, 'Sanktuarium w Krzeszowie to ważne miejsce pielgrzymkowe, które przyciąga wiernych swoją piękną architekturą i atmosferą skupienia.'),
    ('Muzeum Papiernictwa w Dusznikach-Zdroju', POINT(50.4030, 16.3909), 'urbanistyka', 'muzeum', 7, 90, NULL, 'Muzeum Papiernictwa w Dusznikach-Zdroju to fascynujące miejsce, gdzie można poznać historię produkcji papieru i techniki związane z papiernictwem.'),
    ('Wodospad Kamieńczyka', POINT(50.7975, 15.4891), 'natura', 'wodospad', 8, 60, NULL, 'Wodospad Kamieńczyka to malowniczy wodospad położony w sercu Karkonoszy, oferujący przyjemne wrażenia wizualne i możliwość relaksu w otoczeniu natury.'),
    ('Zamek Chojnik', POINT(50.8673, 15.6823), 'urbanistyka', 'ruiny', 7, 90, NULL, 'Zamek Chojnik to malownicza ruina zamku położona na szczycie góry Chojnik w Górach Izerskich. Mimo że zamek nie jest w pełni zrekonstruowany, to wciąż przyciąga turystów swoją historią i zapierającymi dech w piersiach widokami na okolicę.'),
    ('Podziemne Miasta Riese', POINT(50.6253, 16.5095), 'urbanistyka', 'kompleks podziemny', 8, 120, NULL, 'Podziemne Miasta Riese to tajemniczy kompleks podziemnych tuneli i korytarzy z czasów II wojny światowej. Jest to fascynujące miejsce dla miłośników historii i tajemniczych zagadek.'),
    ('Arboretum Wojsławice', POINT(50.7078, 16.8550), 'natura', 'ogród botaniczny', 9, 120, NULL, 'Arboretum Wojsławice to piękny ogród botaniczny, który zachwyca różnorodnością roślin i malowniczym otoczeniem. Jest to doskonałe miejsce na spokojny spacer i relaks na łonie natury.'),
    ('Hala Stulecia we Wrocławiu', POINT(51.1065, 17.0775), 'urbanistyka', 'zabytek architektury', 8, 60, NULL, 'Hala Stulecia we Wrocławiu to imponujący zabytek architektury, będący symbolem miasta. Jest to miejsce organizacji różnorodnych wydarzeń kulturalnych i sportowych.'),
    ('Ogród Japoński we Wrocławiu', POINT(51.1070, 17.0753), 'natura', 'ogród', 7, 60, NULL, 'Ogród Japoński we Wrocławiu to spokojne miejsce, gdzie można odpocząć wśród pięknie zaprojektowanych stawów, mostków i roślinności charakterystycznej dla ogrodów japońskich.'),
    ('Kolejkowo we Wrocławiu', POINT(51.1094, 17.0223), 'urbanistyka', 'makieta kolejowa', 7, 60, NULL, 'Kolejkowo we Wrocławiu to unikatowa makieta kolejowa, prezentująca krajobraz i życie miasta we wspaniałych detalach.'),
    ('Hydropolis we Wrocławiu', POINT(51.1104, 17.0475), 'urbanistyka', 'centrum edukacyjne', 8, 90, NULL, 'Hydropolis we Wrocławiu to interaktywne centrum edukacyjne poświęcone wodzie i jej roli w życiu na Ziemi. Jest to fascynujące miejsce dla osób w każdym wieku.'),
    ('ZOO Wrocław', POINT(51.1042, 17.0769), 'urbanistyka', 'ogród zoologiczny', 9, 180, NULL, 'ZOO Wrocław to jedno z największych i najbardziej atrakcyjnych ogrodów zoologicznych w Polsce. Oferuje ono możliwość obserwacji bogatej fauny z całego świata.'),
    ('Kolorowe Jeziorka', POINT(50.8172, 15.7540), 'natura', 'zbiorniki wodne', 7, 90, NULL, 'Kolorowe Jeziorka to grupa malowniczych zbiorników wodnych w Karkonoszach, których niezwykły kolor przyciąga wielu turystów.'),
    ('Bazylika w Bardzie', POINT(50.5128, 16.8959), 'urbanistyka', 'bazylika', 8, 60, NULL, 'Bazylika w Bardzie to imponujący zabytek sakralnej architektury, który przyciąga wiernych i turystów swoim pięknem i duchową atmosferą.'),
    ('Zamek Bolczów', POINT(50.8694, 15.8890), 'urbanistyka', 'ruiny', 6, 90, NULL, 'Zamek Bolczów to malownicza ruina zamku w Kotlinie Kamiennogórskiej, która zachwyca swoją historią i położeniem wśród górskich krajobrazów.'),
    ('Zamek w Leśnicy', POINT(51.1142, 16.9896), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek w Leśnicy to urokliwa warownia wznosząca się na wzgórzu w Górach Kaczawskich. Oferuje ona możliwość poznania historii regionu oraz piękne widoki na okolicę.'),
    ('Zamek w Świebodzicach', POINT(50.8622, 16.3199), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek w Świebodzicach to historyczna warownia, która stanowi ważny element dziedzictwa kulturowego Dolnego Śląska. Oferuje ona możliwość eksploracji oraz podziwiania malowniczej okolicy.'),
    ('Zamek w Niemczy', POINT(50.7275, 16.8356), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Niemczy to malownicza warownia, która pełniła ważną rolę w historii regionu. Dzisiaj stanowi ona atrakcyjny punkt turystyczny, przyciągając miłośników historii i architektury.'),
    ('Zamek w Siedlęcinie', POINT(50.9769, 15.8755), 'urbanistyka', 'zamek', 7, 60, NULL, 'Zamek w Siedlęcinie to urokliwa warownia, która stanowi doskonały przykład średniowiecznej architektury obronnej. Jest to fascynujące miejsce dla miłośników historii i romantycznych krajobrazów.'),
    ('Zamek w Bolkowie', POINT(50.9189, 16.0997), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek w Bolkowie to imponująca średniowieczna warownia, która dominuje nad miastem. Z jego murów roztacza się piękny widok na okolicę, a bogata historia przyciąga wielu turystów.'),
    ('Zamek w Oławie', POINT(50.9450, 17.2922), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Oławie to historyczna rezydencja książąt oławskich, która zachwyca swoją architekturą i malowniczym położeniem. Jest to doskonałe miejsce na spacer i poznanie historii regionu.'),
    ('Zamek w Grodźcu', POINT(50.6845, 16.3640), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek w Grodźcu to imponująca warownia na wzgórzu, znana z licznych imprez kulturalnych i rekonstrukcji historycznych. Zamek oferuje piękne widoki i fascynującą podróż w przeszłość.'),
    ('Zamek w Pieskowej Skale', POINT(50.2079, 19.8069), 'urbanistyka', 'zamek', 8, 90, NULL, 'Zamek w Pieskowej Skale to perła renesansowej architektury, malowniczo położona w Dolinie Prądnika. Jest częścią Ojcowskiego Parku Narodowego i oferuje bogate zbiory muzealne oraz piękne ogrody.'),
    ('Zamek w Mosznej', POINT(50.4325, 17.7836), 'urbanistyka', 'zamek', 9, 120, NULL, 'Zamek w Mosznej to jedna z najbardziej rozpoznawalnych rezydencji w Polsce, znana ze swojej bajkowej architektury i pięknych ogrodów. Jest to doskonałe miejsce na dłuższy spacer i zwiedzanie.'),
    ('Zamek w Karpnikach', POINT(50.8336, 15.7503), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek w Karpnikach to elegancka rezydencja, otoczona malowniczym parkiem krajobrazowym. Jest to miejsce o bogatej historii, idealne na romantyczny weekend lub kulturalne wydarzenie.'),
    ('Zamek w Starej Kamienicy', POINT(50.9167, 15.5000), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Starej Kamienicy to malownicza ruina położona wśród górskich krajobrazów. Choć zamek nie jest w pełni zrekonstruowany, to jego historia i położenie przyciągają wielu turystów.'),
    ('Zamek w Wojcieszowie', POINT(50.9167, 15.8333), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Wojcieszowie to urokliwa warownia położona na wzgórzu, oferująca piękne widoki na okolicę. Jest to doskonałe miejsce na spokojny spacer i poznanie lokalnej historii.'),
    ('Zamek w Książęcej Górze', POINT(50.9167, 15.8333), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Książęcej Górze to malownicza warownia, która zachwyca swoją architekturą i położeniem w otoczeniu górskich krajobrazów. Jest to miejsce pełne historii i pięknych widoków.'),
    ('Zamek w Rogowie Sobóckim', POINT(51.1333, 17.2000), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Rogowie Sobóckim to historyczna rezydencja położona w malowniczym otoczeniu. Jest to doskonałe miejsce na spacer i poznanie historii regionu.'),
    ('Zamek w Bystrzycy Kłodzkiej', POINT(50.3000, 16.6500), 'urbanistyka', 'zamek', 7, 90, NULL, 'Zamek w Bystrzycy Kłodzkiej to malownicza warownia, która oferuje fascynującą podróż w przeszłość. Zamek jest doskonałym punktem widokowym na miasto i okolice.'),
    ('Zamek w Ścinawce Średniej', POINT(50.6167, 16.5167), 'urbanistyka', 'zamek', 6, 60, NULL, 'Zamek w Ścinawce Średniej to malownicza warownia położona wśród górskich krajobrazów. Jest to miejsce o bogatej historii, idealne na spokojny spacer i poznanie lokalnej kultury.')
